     */
    function edit($id)
    {   
        // check if the fip_role exists before trying to edit it
        $data['fip_role'] = $this->Fip_role_model->get_fip_role($id);
        
        if(isset($data['fip_role']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
