     * Deleting fip_role
     */
    function remove($id)
    {
        $fip_role = $this->Fip_role_model->get_fip_role($id);

        // check if the fip_role exists before trying to delete it
        if(isset($fip_role['id']))
        {
            $this->Fip_role_model->delete_fip_role($id);
